<?phpif (empty($id)) {	return FALSE;}?><div id="password_form">	<form id="password_editor" name="password_editor"				action="<?php print site_url("user/change_password") ?>" method="post">		<input type="hidden" name="valid_password" id="valid_password" value=false/>		<input type="hidden" name="id" id="id" value="<?php print $id; ?>"/>		<div id="password_message" class="message alert hidden"></div>		<p><label for="current_password">Current Password: </label>			<input type="password" id="current_password" name="current_password"						 value="" required/></p>		<p>			<label for="new_password">New Password: </label>			<input type="password" id="new_password" name="new_password" value=""						 required/></p>		<p>			<label for="check_password">Re-enter New Password: </label>			<input type="password" id="check_password" name="check_password" value=""						 required/>		</p>		<label for="show_password">Show Password</label>		<input type="checkbox"					 id="show_password"					 name="show_password"					 value="1"/>		<p><input type="submit" class='button change_password' disabled							id='change-password' value='Change Password'/></p>	</form></div>